function get_rand($PrizeList=null) {
    $Bingo = array();
    if($PrizeList){
        //概率陣列的總概率精度
        $proSum = array_sum(array_column(array_column($PrizeList, 'subject'), 'Percent'));//$PrizeList[$key]['subject']['Percent']
        //概率陣列迴圈
        if($PrizeList && $proSum){
            foreach ($PrizeList as $key => $value) {
                $randNum = mt_rand(1, $proSum);
                if ($randNum <= $value['subject']['Percent']) {
                    $Bingo = array(
                        "key" => $key,
                        "value" => $value,
                    );
                    break;
                } else {
                    $proSum -= $value['subject']['Percent'];
                }
            }
        }
    }
    return $Bingo;
}

if(0){
    //測試n筆抽獎
    $PercentTest = array();
    for($i=0;$i<1000;$i++){
        $RandItem = get_rand($TotalPrizes);
        $PercentTest[$RandItem['subject']['subject']] ++;
    }
    $List = array_column(array_column($TotalPrizes, 'subject'), 'Percent', 'subject');
    $Result = array();
    if($List){
        foreach ($List as $subject => $Percent) {
            $Result[$subject] = round((($PercentTest[$subject]-$Percent)/$Percent)*100, 2) . '%';
        }
    }
    print_r($Result);
    exit();
}else{
